class FormJourneyInteractor: FormJourneyBusinessLogic, FormJourneyDataStore
{
  var presenter: FormJourneyPresentationLogic?
  var worker: FormJourneyWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: FormJourney.Something.Request)
  {
    worker = FormJourneyWorker()
    worker?.doSomeWork()
    
